import React, { Component } from 'react';
import { css } from 'emotion';
import bgImage from '../../images/new-title-img.jpg';
import logo from '../../images/logo.png';

const logoMove = () => {
    document.querySelector(`#tfs-logo`).classList.add('logoCenter');
}

export default class Landing extends Component {
    render() {
        return (
            <div className={main}>
                <img id="tfs-logo" className={gameLogo} src={logo} onClick={logoMove} alt="TFS Logo" />
            </div>
        )
    }
}

const main = css`
    height: 100vh;
    width: 100vw;
    background-image: url('${bgImage}');
    background-size: cover;
    background-position: center;
`
const gameLogo = css`
    position: absolute;
    left: 70%;
    top: 8%;
    cursor: pointer;
    animation: fadein 3s;
`
const logoCenter = css`
    animation: centerLogo 3s;
`
